        "url": "http://res.cloudinary.com/contre-pensees/image/upload/v1444160224/bzp27c5uypis4a9prxmn.jpg",
        "secure_url": "https://res.cloudinary.com/contre-pensees/image/upload/v1444160224/bzp27c5uypis4a9prxmn.jpg"
